using System;
using System.Text.RegularExpressions;

namespace Web3Unity.Scripts.Library.Ethers.HDNode.Wordlists
{
    public class WordlistEnglish : Wordlist
    {
        private const string _words =
            "";

        private string[] _wordlist;

        public WordlistEnglish() : base("en")
        {
        }

        private void _loadWords()
        {
            if (_wordlist != null && _wordlist.Length > 0)
                return;

            _wordlist = Regex.Replace(_words, @"[^a-zA-Z]+", " ").Trim().ToLower().Split(' ');

            // TODO: verify BIP39 wordlist
        }


        public override string GetWord(int index)
        {
            _loadWords();
            return _wordlist[index];
        }

        public override int GetWordIndex(string word)
        {
            _loadWords();
            return Array.IndexOf(_wordlist, word);
        }
    }
}